	public function report(){

		$products=DB::table('stock_view')
		->select('name','total_qty',DB::raw('SUM(total_qty) as total_qty'))
		->groupBy('name')
		->get();

		
		return view('pages.erp.stock.report',["products"=>$products]);
	}
}
